import * as React from 'react';
import styled from '../../utils/styled';
import LayoutContainer from '../../containers/LayoutContainer';
import Container from './Container';

const Wrapper = styled('footer')`
  padding: 0.5rem 1.5rem;
  background-color: ${props => props.theme.colors.brand};
  color: ${props => props.theme.colors.white};
  font-family: ${props => props.theme.fonts.headings};
`;

const FooterInner = styled(Container)`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;

  @media (min-width: ${props => props.theme.breakpoints.lg}) {
    flex-direction: row;
  }
`;

const FooterLeft = styled('div')`
  padding-right: 1rem;
`;

const FooterNav = styled('nav')`
  flex: 1 1 auto;
  margin: 1rem 0;
  text-align: center;

  @media (min-width: ${props => props.theme.breakpoints.lg}) {
    margin: 0;
  }
`;

const FooterRight = styled('div')`
  padding-left: 1rem;
`;

const CurrentTheme = styled('span')`
  margin-right: 1rem;
`;

const ThemeSwitcherButton = styled('button')`
  display: inline-block;
  padding: 0.25rem 0.5rem;
  border: 1px solid ${props => props.theme.colors.white};
  border-radius: 3px;
  background-color: ${props => props.theme.colors.white};
  color: ${props => props.theme.colors.brand};
  font-size: 0.8rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover,
  &:focus {
    background-color: transparent;
    color: ${props => props.theme.colors.white};
  }
`;

const Footer: React.SFC = () => (
  <Wrapper>
    <FooterInner>
      <FooterLeft>COVID-19 is here!</FooterLeft>

      <FooterNav>
        <div> No Panic, No Ignorant!</div>
      </FooterNav>
      <FooterRight>
        <LayoutContainer>
          {({ theme, setTheme }) => (
            <>
              <CurrentTheme>Current theme: {theme}</CurrentTheme>
              <ThemeSwitcherButton onClick={() => setTheme(theme === 'light' ? 'dark' : 'light')}>Switch theme</ThemeSwitcherButton>
            </>
          )}
        </LayoutContainer>
      </FooterRight>
    </FooterInner>
  </Wrapper>
);

export default Footer;
