import * as React from 'react';
import { connect } from 'react-redux';
import { RouteComponentProps } from 'react-router';

import {
  VideoInfobox,
  VideoInfoboxImage,
  VideoInfoboxHeading,
  VideoInfoboxInner,
  VideoInfoboxBlurBackground,
  VideoName,
  VideoRoles
} from '../../components/videos/VideoInfobox';
import { VideoStats, VideoStatsInner, StatAttribute, Bullet } from '../../components/videos/VideoStats';
import { VideoDetails, VideoDetailsColumn, VideoDetailsRow, VideoDetailsAttrName } from '../../components/videos/VideoDetails';
import Page from '../../components/layout/Page';
import Container from '../../components/layout/Container';

import { ApplicationState } from '../../store';
import { Video } from '../../store/videos/types';
import { fetchRequest } from '../../store/videos/actions';
import styled from '../../utils/styled';
import LoadingOverlay from '../../components/data/LoadingOverlay';
import LoadingOverlayInner from '../../components/data/LoadingOverlayInner';
import LoadingSpinner from '../../components/data/LoadingSpinner';

// Separate state props + dispatch props to their own interfaces.
interface PropsFromState {
  loading: boolean;
  data: Video[];
  errors?: string;
}

// We can use `typeof` here to map our dispatch types to the props, like so.
interface PropsFromDispatch {
  fetchVideos: typeof fetchRequest;
}

interface RouteParams {
  name: string;
}

interface State {
  selected?: Video;
}

// Combine both state + dispatch props - as well as any props we want to pass - in a union type.
type AllProps = PropsFromState & PropsFromDispatch & RouteComponentProps<RouteParams>;

const API_ENDPOINT = process.env.REACT_APP_API_ENDPOINT || 'https://api.opendota.com';

const Wrapper = styled('div')`
  position: relative;
`;

class ShowVideosPage extends React.Component<AllProps, State> {
  constructor(props: AllProps) {
    super(props);

    this.state = {};
  }

  public componentDidMount() {
    const { data, fetchVideos } = this.props;

    if (!data || data.length === 0) {
      fetchVideos();
    }
  }

  public render() {
    const { data, loading, match } = this.props;
    const selected = data.find(hero => hero.name === match.params.name);

    return (
      <Page>
        <Container>
          <Wrapper>
            {loading && (
              <LoadingOverlay>
                <LoadingOverlayInner>
                  <LoadingSpinner />
                </LoadingOverlayInner>
              </LoadingOverlay>
            )}
            {selected && (
              <>
                <VideoInfobox>
                  <VideoInfoboxBlurBackground src={API_ENDPOINT + selected.img} />
                  <VideoInfoboxInner>
                    <VideoInfoboxImage src={API_ENDPOINT + selected.img} />
                    <VideoInfoboxHeading>
                      <VideoName>{selected.localized_name}</VideoName>
                      <VideoRoles>
                        {selected.attack_type} - <span>{selected.roles.join(', ')}</span>
                      </VideoRoles>
                    </VideoInfoboxHeading>
                    <VideoStats>
                      <VideoStatsInner>
                        <StatAttribute attr="str" isPrimaryAttr={selected.primary_attr === 'str'}>
                          <Bullet attr="str" /> {selected.base_str || 0} + {selected.str_gain || 0}
                        </StatAttribute>
                        <StatAttribute attr="agi" isPrimaryAttr={selected.primary_attr === 'agi'}>
                          <Bullet attr="agi" /> {selected.base_agi || 0} + {selected.agi_gain || 0}
                        </StatAttribute>
                        <StatAttribute attr="int" isPrimaryAttr={selected.primary_attr === 'int'}>
                          <Bullet attr="int" /> {selected.base_int || 0} + {selected.int_gain || 0}
                        </StatAttribute>
                      </VideoStatsInner>
                    </VideoStats>
                  </VideoInfoboxInner>
                </VideoInfobox>
                <VideoDetails>
                  <VideoDetailsColumn>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Base Attack:</VideoDetailsAttrName> {selected.base_attack_min} - {selected.base_attack_max}
                    </VideoDetailsRow>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Attack Range:</VideoDetailsAttrName> {selected.attack_range || '-'}
                    </VideoDetailsRow>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Attack Speed:</VideoDetailsAttrName> {selected.attack_speed || '-'}
                    </VideoDetailsRow>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Projectile Speed:</VideoDetailsAttrName> {selected.projectile_speed || '-'}
                    </VideoDetailsRow>
                  </VideoDetailsColumn>
                  <VideoDetailsColumn>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Health:</VideoDetailsAttrName> {selected.base_health || 0}
                    </VideoDetailsRow>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Health Regen:</VideoDetailsAttrName> {selected.base_health_regen || 0}
                    </VideoDetailsRow>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Mana:</VideoDetailsAttrName> {selected.base_mana || 0}
                    </VideoDetailsRow>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Mana Regen:</VideoDetailsAttrName> {selected.base_mana_regen || 0}
                    </VideoDetailsRow>
                  </VideoDetailsColumn>
                  <VideoDetailsColumn>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Base Armor:</VideoDetailsAttrName> -
                    </VideoDetailsRow>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Magic Resistance:</VideoDetailsAttrName> {selected.base_mr || 0}%
                    </VideoDetailsRow>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Move Speed:</VideoDetailsAttrName> {selected.move_speed || 0}
                    </VideoDetailsRow>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Turn Speed:</VideoDetailsAttrName> {selected.turn_rate || 0}
                    </VideoDetailsRow>
                  </VideoDetailsColumn>
                  <VideoDetailsColumn>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>Number of Legs:</VideoDetailsAttrName> {selected.legs}
                    </VideoDetailsRow>
                    <VideoDetailsRow>
                      <VideoDetailsAttrName>CM Enabled:</VideoDetailsAttrName> {selected.cm_enabled ? 'yes' : 'no'}
                    </VideoDetailsRow>
                  </VideoDetailsColumn>
                </VideoDetails>
              </>
            )}
          </Wrapper>
        </Container>
      </Page>
    );
  }
}

// It's usually good practice to only include one context at a time in a connected component.
// Although if necessary, you can always include multiple contexts. Just make sure to
// separate them from each other to prevent prop conflicts.
const mapStateToProps = ({ videos }: ApplicationState) => ({
  loading: videos.loading,
  errors: videos.errors,
  data: videos.data
});

// mapDispatchToProps is especially useful for constraining our actions to the connected component.
// You can access these via `this.props`.
const mapDispatchToProps = {
  fetchVideos: fetchRequest
};

// Now let's connect our component!
// With redux v4's improved typings, we can finally omit generics here.
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ShowVideosPage);
