import * as React from 'react';
import styled from '../../utils/styled';

type ButtonGroupProps = {
  onClick: (event: React.SyntheticEvent<HTMLButtonElement>) => void;
  children: React.ReactElement[];
};

const GroupContainer = styled('div')`
  text-align: center;
`;

const ButtonGroup: React.FC<ButtonGroupProps> = ({ onClick, selected, children }) => {
  const [selectedIndex, setSelectedIndex] = React.useState(0);
  const handleClick = () => {};
  const count = React.Children.count(children);
  return (
    <GroupContainer>
      {React.Children.map(children, (child: React.ReactElement, i: number) => {
        let groupPosition = '';
        if (i === 0) {
          groupPosition = 'first';
        } else if (i === count - 1) {
          groupPosition = 'last';
        }
        return React.cloneElement(child, { groupPosition, onClick: handleClick, 'data-index': i, classname: 'selected' });
      })}
    </GroupContainer>
  );
};

export default ButtonGroup;
